import React from "react";
import { Box, Button, IconButton, InputAdornment, TextField } from "@mui/material";
import SendIcon from "@mui/icons-material/Send";

const Footer = ({
  inputText,
  setInputText,
  generateResponse,
  isGenerating,
}) => {
  return (
    <Box
      sx={{
        display: "flex",
        position: "fixed",
        bottom: 0,
        left: 0,
        right: 0,
        color: "white",
        width: "100%",
        padding: "10px",
        justifySelf: "center",
        marginTop: "20px",
        backgroundColor: "white",
      }}
    >
      <Box
        sx={{
          display: "flex",
          width: { xs: "90%", sm: "80%", md: "70%" },
          margin: "0 auto",
        }}
      >
        <TextField
          variant="outlined"
          fullWidth
          value={inputText}
          sx={{
            "& .MuiOutlinedInput-root": {
              "& fieldset": {
                borderColor: "#B2B2B2",
                borderRadius: "15px",
                height: { xs: "45px", sm: "50px" }, // Responsive height
                padding: "2px",
              },
              "&:hover fieldset": { borderColor: "black" },
              "&.Mui-focused fieldset": { borderColor: "#EC625F" },
            },
            "& .MuiInputBase-input": {
              color: "black",
              fontSize: { xs: "14px", sm: "16px" }, // Responsive font size
            },
            input: { color: "black" },
          }}
          onChange={(e) => setInputText(e.target.value)}
          placeholder="Type your message..."
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <IconButton
                  onClick={generateResponse}
                  color="primary"
                  sx={{
                    color:
                      isGenerating || !inputText.trim() ? "#B2B2B2" : "#EC625F",
                  }}
                  disabled={isGenerating || !inputText.trim()}
                >
                  <SendIcon />
                </IconButton>
              </InputAdornment>
            ),
          }}
        />
        <Button
          variant="contained"
          onClick={generateResponse}
          onKeyPress={(e) => e.key === "Enter" && generateResponse()}
          sx={{
            marginLeft: "8px",
            backgroundColor: "#EC625F",
            color: "white",
            height: { xs: "45px", sm: "50px" }, // Responsive height
            borderRadius: "15px",
            width: { xs: "70px", sm: "80px" }, // Responsive width
            fontSize: { xs: "14px", sm: "16px" }, // Responsive font size
          }}
          disabled={isGenerating || !inputText.trim()}
        >
          Send
        </Button>
      </Box>
    </Box>
  );
};

export default Footer;
