import React, { useState } from "react";
import { Container } from "@mui/material";
import { Light as SyntaxHighlighter } from "react-syntax-highlighter";
import { paraisoDark } from "react-syntax-highlighter/dist/esm/styles/hljs";
import logo from "../images/d753cd31d66585bc9c91332b8a7b25fc-removebg-preview.png";
import Header from "./Header";
import ChatBox from "./ChatBox";
import Footer from "./Footer";

const BaseLayout = () => {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState("");
  const [isGenerating, setIsGenerating] = useState(false);

  const renderContent = (text) => {
    const codeRegex = /```([\s\S]*?)```/g;
    let codeBlocks = [];
    let match;

    while ((match = codeRegex.exec(text)) !== null) {
      codeBlocks.push(match[1]);
    }

    let formattedText = text.replace(codeRegex, "").trim();

    let formattedElements = [];
    const lines = formattedText.split("\n");

    lines.forEach((line, index) => {
      if (line.startsWith("**") && lines[index + 1]?.startsWith("* ")) {
        const title = line.replace("**", "").trim();
        const mainPoint = lines[index + 1].replace("*", "").trim();
        formattedElements.push(
          <div key={index}>
            <h3>{title}</h3>
            <p>{mainPoint}</p>
          </div>
        );
      } else if (line.includes("**")) {
        const boldText = line
          .split("**")
          .map((part, idx) =>
            idx % 2 === 1 ? <strong key={idx}>{part}</strong> : part
          );
        formattedElements.push(<p key={index}>{boldText}</p>);
      } else if (line.startsWith("* ")) {
        formattedElements.push(
          <ul
            key={index}
            style={{ listStyleType: "none", paddingLeft: "20px" }}
          >
            <li style={{ position: "relative", paddingLeft: "20px" }}>
              <span
                style={{
                  position: "absolute",
                  left: "0",
                  top: "0",
                  fontWeight: "bold",
                }}
              >
                •
              </span>
              {line.replace("*", "").trim()}
            </li>
          </ul>
        );
      } else {
        formattedElements.push(<p key={index}>{line}</p>);
      }
    });

    const codeElements = codeBlocks.map((code, index) => (
      <div
        key={index}
        style={{
          marginBottom: "10px",
          backgroundColor: "#525252",
          padding: "10px",
          borderRadius: "15px",
        }}
      >
        <SyntaxHighlighter language="auto" style={paraisoDark}>
          {code}
        </SyntaxHighlighter>
      </div>
    ));

    return [...formattedElements, ...codeElements];
  };

const generateResponse = async (selectedFile) => {
  if (!inputText.trim() && !selectedFile) return;

  setIsGenerating(true);

  try {
    let response;
    const formData = new FormData();

    // If a file is selected, we read it as base64 and append it to FormData
    if (selectedFile) {
      const imageBase64 = Buffer.from(
        await selectedFile.arrayBuffer()
      ).toString("base64");
      formData.append(
        "image",
        JSON.stringify({
          inlineData: {
            data: imageBase64,
            mimeType: selectedFile.type,
          },
        })
      );
    }

    // Prepare the user message, it could be either from the input text or empty
    const messageContent = inputText.trim() ? inputText : "";

    // Append the message along with the image if available to FormData
    formData.append(
      "message",
      JSON.stringify([...messages, { role: "user", content: messageContent }])
    );

    response = await fetch("http://localhost:3001/api/chat", {
      method: "POST",
      mode: "cors",
      body: formData,
    });

    const data = await response.json();

    if (data.status === "success") {
      const assistantResponse = { role: "assistant", content: data.response };
      setMessages((prevMessages) => [
        ...prevMessages, // Add previous messages
        assistantResponse, // Add assistant's response
      ]);
    } else {
      console.error("API Error:", data.error);
    }
  } catch (error) {
    console.error("Request Error:", error);
  } finally {
    setIsGenerating(false);
  }
};




  return (
    <Container
      lg={10}
      sx={{
        minHeight: "96vh",
        display: "flex",
        flexDirection: "column",
        fontFamily: "poppins",
        scrollbarWidth: "none",
        "&::-webkit-scrollbar": {
          display: "none",
        },
      }}
    >
      <Header logo={logo} />
      <ChatBox
        messages={messages}
        isGenerating={isGenerating}
        renderContent={renderContent}
      />
      <Footer
        inputText={inputText}
        setInputText={setInputText}
        generateResponse={generateResponse}
        isGenerating={isGenerating}
      />
    </Container>
  );
};

export default BaseLayout;
